import { RequestHeaders } from "@octokit/types/src/RequestHeaders";
import { RequestMethod } from "@octokit/types/src/RequestMethod";
import { RequestParameters } from "@octokit/types/src/RequestParameters";
import { Url } from "@octokit/types/src/Url";

/**
 * The `.endpoint()` method is guaranteed to set all keys defined by RequestParameters
 * as well as the method property.
 */
export type EndpointDefaults = RequestParameters & {
  baseUrl: Url;
  method: RequestMethod;
  url?: Url;
  headers: RequestHeaders & {
    accept: string;
    "user-agent": string;
  };
  mediaType: {
    format: string;
    previews: string[];
  };
};
