import { RequestHeaders } from "@octokit/types/src/RequestHeaders";
import { RequestMethod } from "@octokit/types/src/RequestMethod";
import { RequestRequestOptions } from "@octokit/types/src/RequestRequestOptions";
import { Url } from "@octokit/types/src/Url";

/**
 * Generic request options as they are returned by the `endpoint()` method
 */
export type RequestOptions = {
  method: RequestMethod;
  url: Url;
  headers: RequestHeaders;
  body?: any;
  request?: RequestRequestOptions;
};
